import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../css/Pay.css';
import OrderModal from '../util/orderModal';
import DateFilter from '../DateFilter';

  const Stock = () => {
  const [stockList, setStockTotalList] = useState([]);
  const REST = process.env.REACT_APP_REST;
  const [showOrderModal, setShowOrderModal] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);
  const [filterPlaces, setFilterPlaces] = useState([]);
    const [placeNames, setPlaceNames] = useState([]);

  useEffect(() => {
    axios.get(`${REST}/api/stocktotalList`)
      .then(response => {
        setStockTotalList(response.data)
        
      })
      .catch(error => {
        console.error('재고 목록을 불러오는 데 실패했습니다:', error);
      });
  }, []);

  return (
    <div className="pay-container">
      <table className="pay-table">
         {/* ✅ 필터 영역 */}
      <div className="pay-filters">
        <button className="date-btn" onClick={() => setShowDateFilter(true)}>DATE</button>

        <input
          type="text"
          placeholder="메뉴명을 입력해주세요."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />

        {placeNames.map((placeName) => (
          <button
            key={placeName}
            className={`place-btn ${filterPlaces.includes(placeName) ? 'selected' : ''}`}
            onClick={() => handlePlaceFilter(placeName)}
          >
            {placeName}
          </button>
        ))}
      </div>
          <thead>
            <tr>
              <th>NO</th>
              <th>지점</th>
              <th>메뉴명</th>
              <th>이미지파일주소</th>
              <th>재고량</th>
              <th>상품상태</th>
              <th>발주상태</th>
              <th>발주하기</th>
            </tr>
          </thead>
          <tbody>
            {stockList.map((item, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{item.place_name}</td>
                <td>{item.menu_name}</td>
                <td>{item.img_path}</td>
                <td>
                  {item.stock_qty === 0 ? (
                    <span className="badge-outofstock">
                      <span className="dot purple" /> 재고없음
                    </span>
                  ) : (
                    item.stock_qty
                  )}
                </td>


                <td>{item.product_state ? '판매중' : '판매중단'}</td>
              

                <td id="orderState">
                {item.order_state == null ? null : (
                  Number(item.order_state) === 0 ? (
                    <span className="badge badge-waiting">
                      <span className="dot orange" /> 발주됨
                    </span>
                  ) : Number(item.order_state) === 1 ? (
                    <span className="badge badge-progress">
                      <span className="dot purple" /> 배송중
                    </span>
                  ) : Number(item.order_state) === 2 ? (
                    <span className="badge badge-success">
                      <span className="dot green" /> 확인중
                    </span>
                  ) : (
                    <span className="badge badge-unknown">
                      <span className="dot gray" /> 알 수 없음
                    </span>
                  )
                )}
              </td>


                <td>
                  <button
                    onClick={() => {
                      setSelectedItem(item);
                      setShowOrderModal(true);
                    }}
                  >
                    <img src="/Icon.png" alt="발주" />
                  </button>
                  
                </td>
              </tr>
            ))}
          </tbody>
        
      </table>

      {showOrderModal && selectedItem && (
        <OrderModal
          item={selectedItem}
          onClose={() => setShowOrderModal(false)}
          onConfirm={(qty) => { // ✅ 모달에서 받은 발주 수량 qty
            axios.post(`${REST}/api/order/request`, {
              menu_id: selectedItem.menu_id,
              place_id: selectedItem.place_id,
              amount: qty,
            }).then(() => {
              alert(`${selectedItem.menu_name} ${qty}개 발주 완료!`);
              console.log('발주 요청 데이터:', {
                menu_id: selectedItem.menu_id,
                place_id: selectedItem.place_id,
                amount: qty,
              });
              setShowOrderModal(false);
            }).catch(err => {
              console.error("발주 실패:", err);
              alert("발주 요청 중 오류가 발생했습니다.");
            });
          }}
            />
          )}
    </div>
  );
};

export default Stock;
