import { useState,useEffect } from "react";
import { useNavigate } from "react-router-dom";
import DateFilter from "../DateFilter";
import axios from "axios";
import { AddMenu } from "./AddMenu";

export function Head_Center(props){
    const [payList, setPayList] = useState([]);
    const REST = process.env.REACT_APP_REST;
    const navigate = useNavigate();
    const [showDateFilter, setShowDateFilter] = useState(false);  
    const handleDataSubmit = (filteredData) => {
      setPayList(filteredData);  
    };
  
    const handleChangeOrder = (idx, e) => {
      console.log("보내는 값 확인:", idx, e.target.value); // 🔍 여기 추가
    
      axios.post(`${REST}/admin/setStockState`, {
        id: idx,
        state: e.target.value
      })
      .then(() => console.log("상태 변경 성공"))
      .catch((err) => console.error("상태 변경 실패", err));
    };
    
    
    useEffect(() => {
      fetch(`${REST}/api/stocktotalList`)
        .then((res) => res.json())
        .then((data) => {
          console.log('결제 목록:', data);
          setPayList(data);
        })
        .catch((err) => {
          console.error('결제 리스트 불러오기 실패:', err);
        });
    }, []);
  
    return (
      <div className="admin-pay-container">
        <button onClick={() => setShowDateFilter(true)}>DATE</button>
        {showDateFilter && (
          <>
            <div className="admin-overlay" onClick={() => setShowDateFilter(false)}></div>          
            <div className="admin-modal">
              <DateFilter onSubmit={handleDataSubmit} />
            </div>
            </>
        )}
  
        <table className="admin-pay-table">
          <thead>
            <tr>
              <th>NO</th>
              <th>지점명</th>
              <th>메뉴이름</th>
              <th>재고량</th>
              <th>판매상태</th>
              <th>발주상태</th>
            </tr>
          </thead>
          <tbody>
            {payList.map((item, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{item.place_name}</td>
                <td>{item.menu_name}</td>
                <td>{item.stock_qty}</td>
                <td>{item.product_state? "판매중" : "판매중단"}</td>
                <td id="orderState">
                  {item.order_state == -1? null : 
                    <select onChange={(e) => handleChangeOrder(item.out_id,e)} id="orderState" defaultValue={item.order_state}>
                        <option value={0}>발주됨</option>
                        <option value={1}>배송중</option>
                        <option value={2}>확인중</option>
                    </select> }
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
}
