import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import DateFilter from "../DateFilter";
import axios from "axios";
import "../css/OrderModal.css";

export function Head_Center(props) {
  const [payList, setPayList] = useState([]);
  const [pendingChange, setPendingChange] = useState(null); // 변경 대기 중인 값 저장
  const [showConfirmModal, setShowConfirmModal] = useState(false); // 모달 상태
  const REST = process.env.REACT_APP_REST;
  const navigate = useNavigate();
  const [showDateFilter, setShowDateFilter] = useState(false);

  useEffect(() => {
    fetch(`${REST}/admin/GetStock`)
      .then((res) => res.json())
      .then((data) => setPayList(data))
      .catch((err) => console.error('불러오기 실패:', err));
  }, []);

  const handleDataSubmit = (filteredData) => setPayList(filteredData);

  const handleChangeOrder = (idx, e) => {
    const selectedValue = parseInt(e.target.value, 10);
    setPendingChange({ id: idx, value: selectedValue });
    setShowConfirmModal(true);
  };

  const confirmChange = () => {
    if (!pendingChange) return;
    const { id, value } = pendingChange;

    axios.post(`${REST}/admin/setStockState`, null, {
      params: { id, state: value }
    })
      .then(res => {
        alert("상태 변경 성공!");
        setShowConfirmModal(false);
        setPendingChange(null);
      })
      .catch(err => {
        console.error("상태 변경 실패:", err);
        alert("서버 오류로 상태 변경에 실패했습니다.");
        setShowConfirmModal(false);
        setPendingChange(null);
      });
  };

  return (
    <div className="admin-pay-container">
      <button onClick={() => setShowDateFilter(true)}>DATE</button>
      {showDateFilter && (
        <>
          <div className="admin-overlay" onClick={() => setShowDateFilter(false)}></div>
          <div className="admin-modal">
            <DateFilter onSubmit={handleDataSubmit} />
          </div>
        </>
      )}

      <table className="admin-pay-table">
        <thead>
          <tr>
            <th>NO</th>
            <th>재고량</th>
            <th>메뉴이름</th>
            <th>지점명</th>
            <th>판매상태</th>
            <th>발주상태</th>
          </tr>
        </thead>
        <tbody>
          {payList.map((item, index) => (
            <tr key={item.id}>
              <td>{index + 1}</td>
              <td>{item.amount}</td>
              <td>{item.flavor_name}</td>
              <td>{item.place_name}</td>
              <td>{item.selling ? "판매중" : "판매중단"}</td>
              <td>
                {item.inOrder == -1 ? null :
                  <select
                    onChange={(e) => handleChangeOrder(item.out_id, e)}
                    defaultValue={item.inOrder}
                  >
                    <option value={0}>발주됨</option>
                    <option value={1}>배송중</option>
                    <option value={2}>확인중</option>
                  </select>}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* ✅ 모달 */}
      {showConfirmModal && (
        <div className="confirm-modal">
          <div className="confirm-box">
            <p>정말 발주상태를 변경하시겠습니까?</p>
            <div className="button-wrap">
              <button className="yes-btn" onClick={confirmChange}>예</button>
              <button className="no-btn" onClick={() => { setShowConfirmModal(false); setPendingChange(null); }}>아니요</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
